/* Random whole numbers within a range can be generated by rounding down the difference of two numbers (max and min)
plus one, multiplied by a random decimal value between 0 and 1, and then added to the min */

// Example
function ourRandomRange(ourMin, ourMax) {

  return Math.floor(Math.random() * (ourMax - ourMin + 1)) + ourMin;
}

ourRandomRange(1, 9);

// Only change code below this line.

function randomRange(myMin, myMax) {

  return Math.floor(Math.random() * (myMax - myMin + 1 )) + myMin; // Change this line

}

